import { useState, useEffect } from 'react';
import { Box, Container, Skeleton, Typography } from '@material-ui/core';
import { useTheme } from '@material-ui/core/styles';

const HomeHero = (props) => {
  const theme = useTheme();
  const [isLoading, setIsLoading] = useState(true);
  const [image, setImage] = useState('');

  useEffect(() => {
    (async () => {
      const response = await fetch(`/static/home/hero_${theme.palette.mode}.png`);
      const blob = await response.blob();

      setImage(URL.createObjectURL(blob));
      setIsLoading(false);
    })();
  }, [theme.palette.mode]);

  return (
    <Box
      sx={{
        backgroundColor: 'background.paper',
        pt: 6
      }}
      {...props}
    >
      <Container
        maxWidth="md"
        sx={{
          alignItems: 'center',
          display: 'flex',
          flexDirection: 'column',
          px: {
            md: '130px !important'
          }
        }}
      >
        <Typography
          align="center"
          color="textPrimary"
          variant="h3"
          sx={{ 
            mt: 5
          }}
        >
          PERFORMLY
        </Typography>
        <Typography
          align="center"
          color="textSecondary"
          variant="body1"
          sx={{ py: 5 }}
        >
          Video On Demand Streaming Platform
        </Typography>
      </Container>
      <Container
        maxWidth="lg"
        sx={{
          width: '100%',
          px: {
            md: 15
          }
        }}
      >
        <Box
          sx={{
            position: 'relative',
            pt: !isLoading && '62.13%'
          }}
        >
          {isLoading
            ? (
              <Skeleton
                sx={{
                  borderRadius: 1,
                  width: '100%',
                  pt: '62.13%'
                }}
                variant="rectangular"
              />
            )
            : (
              <div></div>
            )}
          <img
            alt="Shapes 1"
            src="/static/home/shapes_1.svg"
            style={{
              left: '3%',
              position: 'absolute',
              top: '-7.5%',
              width: '20%',
              maxWidth: '243.32px',
              zIndex: 0
            }}
          />
          <img
            alt="Shapes 2"
            src="/static/home/shapes_2.svg"
            style={{
              bottom: '10%',
              position: 'absolute',
              right: '-8%',
              width: '20%',
              maxWidth: '195.32px',
              zIndex: 30
            }}
          />
        </Box>
      </Container>
    </Box>
  );
};

export default HomeHero;
