import { useState, useEffect } from 'react';
import { Box, Container, Skeleton, Typography } from '@material-ui/core';
import { useTheme } from '@material-ui/core/styles';

const HomeHero = (props) => {
  const theme = useTheme();
  const [isLoading, setIsLoading] = useState(true);
  const [image, setImage] = useState('');

  useEffect(() => {
    (async () => {
      const response = await fetch(`/static/home/hero_${theme.palette.mode}.png`);
      const blob = await response.blob();

      setImage(URL.createObjectURL(blob));
      setIsLoading(false);
    })();
  }, [theme.palette.mode]);

  return (
    <Box
      sx={{
        backgroundColor: '#fff',
      }}
      {...props}
    >
      <Container
        maxWidth="md"
        sx={{
          alignItems: 'center',
          display: 'flex',
          flexDirection: 'column',
          px: {
            md: '130px !important'
          }
        }}
      >
        <Typography
          align="center"
          color="#222"
          variant="h3"
          sx={{ 
            mt: 5,
            pt: 5
          }}
        >
          American Youth Football King of Spring 2021
        </Typography>
      </Container>
      <Container
        maxWidth="lg"
        sx={{
          width: '100%',
          px: {
            md: 15
          }
        }}
      >
        <Box
          sx={{
            position: 'relative'
          }}
        >
          <Box
            alt="Logo Image"
            component="img"
            src="https://cdn.varsitymedialive.com/wp-content/uploads/2021/05/KOS-logo.png"
            sx={{
              width: '100%',
            }}
          />
        </Box>
      </Container>
    </Box>
  );
};

export default HomeHero;
