import Chart from 'react-apexcharts';
import { format } from 'date-fns';
import { Box, Card, CardHeader, Typography } from '@material-ui/core';
import { useTheme } from '@material-ui/core/styles';
import Scrollbar from '../../Scrollbar';

const data = {
  series: [
    {
      data: [12, 24, 36, 48, 60, 72, 84]
    },
    {
      data: [12, 24, 36, 48, 60, 72, 84]
    },
    {
      data: [12, 24, 36, 48, 60, 72, 84]
    }
  ],
  categories: [
    'Capital One',
    'Ally Bank',
    'ING',
    'Ridgewood',
    'BT Transilvania',
    'CEC',
    'CBC'
  ]
};

const Chart12 = () => {
  const theme = useTheme();

  const chartOptions = {
    chart: {
      background: 'transparent',
      stacked: true,
      toolbar: {
        show: false
      }
    },
    colors: ['#3C4693', '#5664D2', '#7783DB'],
    dataLabels: {
      enabled: false
    },
    grid: {
      borderColor: theme.palette.divider,
      xaxis: {
        lines: {
          show: true
        }
      },
      yaxis: {
        lines: {
          show: true
        }
      }
    },
    states: {
      active: {
        filter: {
          type: 'none'
        }
      },
      hover: {
        filter: {
          type: 'none'
        }
      }
    },
    legend: {
      show: false
    },
    stroke: {
      colors: ['transparent'],
      show: true,
      width: 2
    },
    theme: {
      mode: theme.palette.mode
    },
    xaxis: {
      axisBorder: {
        show: false
      },
      axisTicks: {
        show: false
      },
      categories: data.categories,
      labels: {
        style: {
          colors: theme.palette.text.secondary
        }
      }
    },
    yaxis: {
      labels: {
        offsetX: -12,
        style: {
          colors: theme.palette.text.secondary
        }
      }
    }
  };

  const chartSeries = data.series;

  return (
    <Box
      sx={{
        backgroundColor: 'background.default',
        p: 3
      }}
    >
      <Card>
        <CardHeader
          subheader={(
            <Typography
              color="textSecondary"
              variant="body2"
            >
              {format(new Date(), 'MMM yyyy')}
            </Typography>
          )}
          title="Total Transactions"
        />
        <Scrollbar>
          <Box
            sx={{
              height: 336,
              minWidth: 500,
              px: 2
            }}
          >
            <Chart
              height="300"
              options={chartOptions}
              series={chartSeries}
              type="bar"
            />
          </Box>
        </Scrollbar>
      </Card>
    </Box>
  );
};

export default Chart12;
