import { Box, List, ListItem, ListItemAvatar, ListItemText, Typography } from '@material-ui/core';
import { useTheme } from '@material-ui/core/styles';

const ColorsSeverity = () => {
  const theme = useTheme();

  const colors = [
    {
      name: 'Success',
      code: theme.palette.success.main
    },
    {
      name: 'Error',
      code: theme.palette.error.main
    },
    {
      name: 'Warning',
      code: theme.palette.warning.main
    },
    {
      name: 'Info',
      code: theme.palette.info.main
    }
  ];

  return (
    <Box
      sx={{
        backgroundColor: 'background.paper',
        p: 3
      }}
    >
      <List
        disablePadding
        sx={{
          '& li:not(:last-child)': {
            pb: '33px',
            pt: 0
          },
          '& li:last-child': {
            py: 0
          }
        }}
      >
        {colors.map((color) => (
          <ListItem
            disableGutters
            key={color.code}
          >
            <ListItemAvatar>
              <Box
                sx={{
                  backgroundColor: color.code,
                  borderRadius: '10px',
                  height: 46,
                  mr: 2,
                  width: 46
                }}
              />
            </ListItemAvatar>
            <ListItemText
              primary={(
                <Typography
                  color="textPrimary"
                  variant="subtitle2"
                >
                  {color.name}
                </Typography>
              )}
              secondary={(
                <Typography
                  color="textSecondary"
                  variant="caption"
                >
                  {color.code}
                </Typography>
              )}
            />
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default ColorsSeverity;
